INSERT INTO `uewp_posts` VALUES (1,1,'2014-01-10 15:55:18','2014-01-10 15:55:18','United Excel (UEC) is a performance-driven healthcare contractor committed to effectively managing and delivering successful projects on time and within budget.  UEC self-performs on-site supervision, quality control, safety, cost estimating, administration and management. The team is composed of project managers, administrators, superintendents and craftsmen who have built their careers on healthcare construction.','United Excel','','publish','open','open','','united-excel','','','2015-04-24 17:30:47','2015-04-24 17:30:47','',0,'http://2020.2020creative.net/?p=1',0,'post','',1),(18,1,'2014-03-31 21:24:38','2014-03-31 21:24:38','','Activity','','trash','closed','closed','','activity','','','2015-04-28 14:31:29','2015-04-28 14:31:29','',0,'http://2020wp.2020creative.net/?page_id=18',0,'page','',0),(19,1,'2014-03-31 21:24:38','2014-03-31 21:24:38','','Members','','trash','closed','closed','','members','','','2015-04-28 14:30:48','2015-04-28 14:30:48','',0,'http://2020wp.2020creative.net/?page_id=19',0,'page','',0),(20,1,'2014-03-31 21:26:12','2014-03-31 21:26:12','','Groups','','trash','closed','closed','','groups','','','2015-04-28 14:32:20','2015-04-28 14:32:20','',0,'http://2020wp.2020creative.net/?page_id=20',0,'page','',0),(21,1,'2014-03-31 21:26:52','2014-03-31 21:26:52','','Register','','trash','open','open','','register','','','2015-04-28 14:32:02','2015-04-28 14:32:02','',0,'http://2020wp.2020creative.net/?page_id=21',0,'page','',0),(31,1,'2015-01-11 17:06:08','2015-01-11 17:06:08','Can you create a cool ticket plugin?','Help creating new ticket plugin','','publish','open','open','','help-creating-new-ticket-plugin','','','2015-01-11 17:47:18','2015-01-11 17:47:18','',0,'http://2020wp.2020creative.net/?post_type=ticket&#038;p=31',0,'ticket','',1),(35,1,'2015-01-21 21:08:57','2015-01-21 21:08:57','','Home','','publish','open','open','','home','','','2015-04-28 14:11:55','2015-04-